import React, { useState } from 'react';
import Header from './components/Header';
import ClaimInput from './components/ClaimInput';
import CredibilityFingerprint from './components/CredibilityFingerprint';
import EvidenceDisplay from './components/EvidenceDisplay';
import EducationalInsights from './components/EducationalInsights';
import { LoadingState, ErrorState, EmptyState } from './components/States';
import { analyzeClaimAPI, analyzeIndustrialAPI } from './utils/api';
import './index.css';

function App() {
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentInput, setCurrentInput] = useState(null);

  const handleAnalyze = async (input) => {
    setLoading(true);
    setError(null);
    setCurrentInput(input);
    
    try {
      let result;
      if (input.mode === 'industrial') {
        // Use industrial analysis for real-time fact-checking
        const content = input.text || input.url;
        result = await analyzeIndustrialAPI(content);
        
        // Transform result to match expected format
        result = {
          ...result,
          claim: result.content || result.claim,
          status: result.verdict === 'TRUE' ? 'TRUE' : 
                  result.verdict === 'FALSE' ? 'FALSE' : 'NEEDS_REVIEW',
          overall_score: result.scores?.overall_credibility || 50,
          analysis_type: 'industrial',
          // Create credibility fingerprint from industrial scores
          credibility_fingerprint: {
            source_credibility: (result.scores?.source_trust || 50) / 100,
            corroboration_count: result.scores?.corroboration || 0,
            language_risk: 1 - ((result.scores?.language_safety || 50) / 100),
            overall_score: (result.scores?.overall_credibility || 50) / 100
          },
          // Create evidence structure
          evidence: {
            supporting_sources: result.scores?.corroboration || 0,
            contradicting_sources: result.scores?.contradiction || 0,
            verdict: result.verdict
          },
          verification_status: {
            status: result.verdict === 'TRUE' ? 'TRUE' : 
                   result.verdict === 'FALSE' ? 'FALSE' : 'NEEDS_REVIEW',
            confidence: (result.scores?.overall_credibility || 50) / 100
          }
        };
      } else {
        // Use standard database-powered analysis
        result = await analyzeClaimAPI(input);
        result.analysis_type = 'standard';
      }
      
      setAnalysis(result);
    } catch (err) {
      setError(err.message);
      setAnalysis(null);
    } finally {
      setLoading(false);
    }
  };

  const handleRetry = () => {
    if (currentInput) {
      handleAnalyze(currentInput);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Input Section */}
        <div className="mb-8">
          <ClaimInput onAnalyze={handleAnalyze} loading={loading} />
        </div>
        
        {/* Results Section */}
        <div className="space-y-8">
          {loading && <LoadingState />}
          
          {error && !loading && (
            <ErrorState error={error} onRetry={handleRetry} />
          )}
          
          {!analysis && !loading && !error && <EmptyState />}
          
          {analysis && !loading && (
            <>
              {/* Credibility Fingerprint */}
              <div className="animate-fade-in">
                <CredibilityFingerprint fingerprint={analysis.credibility_fingerprint} />
              </div>
              
              {/* Evidence Analysis */}
              <div className="animate-fade-in" style={{ animationDelay: '0.2s' }}>
                <EvidenceDisplay
                  evidence={analysis.evidence}
                  verificationStatus={analysis.verification_status}
                  explanation={analysis.explanation}
                />
              </div>
              
              {/* Educational Insights */}
              <div className="animate-fade-in" style={{ animationDelay: '0.4s' }}>
                <EducationalInsights
                  microLesson={analysis.micro_lesson}
                  suspiciousPhrases={analysis.suspicious_phrases}
                  extractedClaim={analysis.extracted_claim}
                  originalInput={analysis.original_input}
                />
              </div>
            </>
          )}
        </div>
      </main>
      
      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 mt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">CredLens</h3>
            <p className="text-gray-600 mb-4">
              AI-powered misinformation detection and media literacy education
            </p>
            <div className="flex justify-center space-x-6 text-sm text-gray-500">
              <span>• Semantic fact-checking</span>
              <span>• Educational micro-lessons</span>
              <span>• Credibility fingerprinting</span>
              <span>• Source verification</span>
            </div>
            <div className="mt-4 text-xs text-gray-400">
              Built with React, Tailwind CSS, FastAPI, and Hugging Face Transformers
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
